--2442_wifi
-- Set FTM mode
local mode = ftm_send('4B0F0000'):gsub(' ', '')
if mode:len() < 18 or mode:sub(17, 18) ~= '01' then
    ftm_send('290300')
    delay(1000)
end
---set radio cofigure
ftm_send('4B0B16000000380000000100000000000500000002000000000000000C00000000000000000000000000000064000000DB7D910300000000')--RadioConfig
--set tx on
ftm_send('4B0B160000004400000001000000000005000000020000000000000018000000000000000000000000000000450000007E77C70B01000000E40000001C5E834400000000')--TxControl:SetTxOn
delay(1000)
ftm_send('')